import React from 'react';
import { Route, Navigate } from 'react-router-dom';
import { UserData } from '../../types';

interface PrivateRouteProps {
	element: React.ReactElement; // 렌더링할 요소
	user: UserData | null; // 사용자 데이터
	requireSignUp?: boolean;
}

const PrivateRoute = ({ element, user, requireSignUp }: PrivateRouteProps) => {
	console.log('로그인 상태:: ', user);

	// 로그인하지 않은 사용자가 접근하는 경우
	if (!user) {
		return <Navigate to="/signIn" replace />;
	}

	// 회원가입한 사용자만 접근할 수 있도록 설정
	if (requireSignUp && !user.uid) {
		return <Navigate to="/" replace />;
	}

	// 로그인을 한 사용자가 Auth 관련 페이지 접근하는 경우
	if (!requireSignUp && user.uid) {
		alert('유효하지 않은 접근입니다');
		return <Navigate to={'/'} replace />;
	}

	// 모든 조건을 통과한 경우 렌더링
	return element;
};

export default PrivateRoute;
